package Lesson00000training.copy;

public class Demo10While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=1;i<=10;i++) {
	System.out.println("我错啦"+i);
	
}
System.out.println("========================");

int i=1; //初始化语句
while(i<=10) { //条件判断
	System.out.println("我错啦"+i); //循环体
	i++; //4.步进语句
}
	}
	
}
